import React from "react";
import styled from "styled-components";
import Page from "../../global/Page";
import ContactAccents from "./components/ContactAccents";
import { useForm } from "react-hook-form";
import Colors from "../../constants/Colors";

const Contact = () => {
    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = (data) => console.log(data);
    return (
        <Page>
            <ContactAccents />
            <Header>
                <p className='header'>Current or prospective clients</p>
                <p className='sub-header'>We are always pleased to hear from you. Please feel free to contact us</p>
            </Header>

            <Form onSubmit={handleSubmit(onSubmit)}>
                {/* register your input into the hook by invoking the "register" function */}
                <input name='example' defaultValue='test' ref={register({ required: true })} />

                {/* include validation with required or other standard HTML validation rules */}
                <input name='exampleRequired' ref={register({ required: true })} />

                <button type='submit'>
                    <p>Send</p>
                </button>
            </Form>
        </Page>
    );
};
const Header = styled.div`
    margin-bottom: 40px;
    .header {
        font-size: 20px;
        margin-bottom: 16px;
    }
    .subheader {
        font-weight: 300;
        font-size: 16px;
    }
`;

const Form = styled.form`
    display: grid;
    grid-row-gap: 24px;

    input {
        padding-bottom: 14px;
        border: none;
        border-bottom: 1 px solid ${Colors.black};
    }
    button {
        padding: 14px;
        background-color: ${Colors.magenta};
    }
`;
export default Contact;
