import React from "react";
import styled from "styled-components";
import Page from "../../global/Page";
import ContactAccents from "./components/ContactAccents";
import { useForm } from "react-hook-form";
import Colors from "../../constants/Colors";

const Contact = () => {
    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = (data) => console.log(data);
    return (
        <Page>
            <ContactAccents />
            <Header>
                <p className='header'>Current or prospective clients</p>
                <p className='sub-header'>We are always pleased to hear from you. Please feel free to contact us</p>
            </Header>

            <Form onSubmit={handleSubmit(onSubmit)}>
                {/* register your input into the hook by invoking the "register" function */}
                <input name='name' defaultValue='' placeholder='Name' ref={register({ required: true, minLength: 5 })} />
                <input name='phone' defaultValue='' placeholder='Phone' ref={register({ required: true })} />
                <input name='email' defaultValue='' placeholder='Email' ref={register({ required: true })} />

                <button type='submit'>
                    <p>Send</p>
                    <div className='accent' />
                </button>
            </Form>
        </Page>
    );
};
const Header = styled.div`
    margin-bottom: 40px;
    .header {
        font-size: 20px;
        margin-bottom: 16px;
    }
    .subheader {
        font-weight: 300;
        font-size: 16px;
    }
`;

const Form = styled.form`
    display: grid;
    grid-row-gap: 64px;

    input {
        padding-bottom: 14px;
        background-color: transparent;
        border: none;
        border-bottom: 1px solid ${Colors.black};
        font-size: 16px;
        color: ${Colors.black};
        :focus {
            outline: none;
        }
        ::placeholder {
            font-weight: 300;
            font-size: 14px;
            color: #9c9c9c;
        }
    }
    button {
        position: relative;
        padding: 14px;
        /* background-color: ${Colors.magenta}; */
        p {
            position: absolute;
            text-align: left;
            padding-left: 24px;
            color: ${Colors.black};
            z-index: 1;
            font-weight: 600;
            font-size: 16px;
        }
        .accent {
            z-index: 0;
            position: absolute;
            left: 0;
            top: 40%;
            background-color: ${Colors.magenta};
            height: 100%;
            width: 100%;
        }
    }
`;
export default Contact;
