import React from "react";
import styled from "styled-components";
import Colors from "../../../constants/Colors";

const TwitterFeed = () => {
    return (
        <Component>
            <TwitterLink>
                <i className='fab fa-twitter' />
                <a href='http://twitter.com' target='blank'>
                    Follow us on twitter
                </a>
            </TwitterLink>

            <Feed>
                <div className='post'>
                    <p className='handle'>
                        AGM Management <span>@AGM</span>
                    </p>
                    <p className='date'>Apr 6th</p>
                    <div className='post-content'>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Officia aliquam culpa facere assumenda rerum adipisci expedita
                        labore at et eligendi!
                    </div>
                </div>
            </Feed>
        </Component>
    );
};
const Component = styled.div`
    display: flex;
    flex-direction: column;
    background-color: ${Colors.black};
    padding: 0 16px 40px 16px;
`;

const TwitterLink = styled.div`
    margin-left: auto;
    margin-right: -16px;
    margin-bottom: 104px;
    background-color: ${Colors.magenta};
    color: ${Colors.white};
    max-width: 252px;
    padding: 18px 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    i {
        margin-right: 11px;
        font-size: 18px;
    }
    a {
        color: ${Colors.white};
    }
`;

const Feed = styled.div`
    display: grid;
    .post {
        color: ${Colors.white};
        display: grid;
        grid-template-areas:
            "handle date"
            "post-content post-content";
        .handle {
            grid-area: handle;
            font-weight: 600;
            font-size: 18px;
        }
        .date {
            grid-area: date;
        }
        .post-content {
            grid-area: post-content;
        }
    }
`;
export default TwitterFeed;
