import React from "react";
import styled from "styled-components";
import Page from "../../global/Page";
import BrandingAccents from "./BrandingAccents";
import TeamAccent from "../../assets/accents/Accent.svg";
import PhilanthropyImage from "../../assets/images/philanthropy.jpeg";
import Colors from "../../constants/Colors";

const Branding = () => {
    return (
        <Page>
            <BrandingAccents />

            <AGMTeam>
                <p className='heading'>AGM Team</p>
                <p className='description'>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit. Exercitationem nesciunt beatae reprehenderit quae aspernatur dolor
                    minima natus quos error quidem cum perspiciatis fugit, autem, provident distinctio voluptatibus doloribus animi eaque, eligendi
                    quibusdam quod ipsam dignissimos. Rerum, voluptates enim! Tempora ratione ab consequatur quis autem ea pariatur voluptates
                    corrupti hic unde.
                </p>
                <img src={TeamAccent} />
            </AGMTeam>

            <Philanthropy>
                <div className='img'>
                    <img src={PhilanthropyImage} alt='Charity Event' />
                    <div className='img-accent'></div>
                </div>
                <p className='heading'>Philanthropy</p>
                <p className='description'>
                    Lorem ipsum dolor sit amet consectetur, adipisicing elit. Eos, consequatur, amet dolorem culpa sit consequuntur quod voluptatibus
                    impedit libero iure esse recusandae perferendis aliquid rerum perspiciatis blanditiis! Voluptates deserunt quia quisquam
                    consequatur id atque labore voluptas reiciendis accusantium! Excepturi minima repellat sed veritatis esse, explicabo rem
                    voluptatem earum sint debitis.
                </p>
                <button>Learn more</button>
            </Philanthropy>
        </Page>
    );
};
const AGMTeam = styled.div`
    display: grid;
    margin-top: 120px;
    margin-bottom: 80px;
    max-width: 1200px;
    grid-template-areas:
        "heading"
        "description"
        "accent";
    .heading {
        grid-area: heading;
        font-weight: 300;
        font-size: 24px;
        margin-bottom: 32px;
    }
    .description {
        grid-area: description;
        font-weight: 300;
        font-size: 16px;
        line-height: 28px;
        margin-bottom: 80px;
    }
    img {
        grid-area: accent;
        justify-self: end;
        margin-right: -200px;
    }
    @media (min-width: 768px) {
        margin-top: 180px;
    }
    @media (min-width: 1024px) {
        grid-template-columns: 6fr 4fr;
        grid-template-areas:
            "heading heading"
            "description accent";
        .heading {
            font-size: 48px;
            letter-spacing: 0;
            font-weight: 200;
            line-height: 32px;
        }
        .description {
            font-size: 18px;
            line-height: 38px;
        }
    }
    @media (min-width: 1200px) {
        margin: auto;
        margin-top: 320px;
        img {
            margin-right: 0;
        }
    }
`;

const Philanthropy = styled.div`
    display: grid;
    grid-template-areas:
        "img"
        "heading"
        "description"
        "button";
    .img {
        grid-area: img;
        position: relative;
        margin-bottom: 32px;
        img {
            width: 287px;
            height: auto;
        }
        .img-accent {
            position: absolute;
            z-index: -1;
            height: 100%;
            width: 287px;
            background-color: ${Colors.magenta};
            left: 10px;
            top: 5px;
        }
    }
    .heading {
        grid-area: heading;
        font-weight: 300;
        font-size: 24px;
        margin-bottom: 24px;
    }
    .description {
        grid-area: description;
        font-weight: 300;
        font-size: 16px;
        line-height: 28px;
        margin-bottom: 32px;
    }
    button {
        grid-area: button;
        justify-self: end;
        padding: 12px 50px;
        text-transform: uppercase;
        font-size: 16px;
    }
`;
export default Branding;
