import React from "react";
import styled from "styled-components";
import Drawer from "@material-ui/core/Drawer";
import { useSelector, useDispatch } from "react-redux";
import { HamburgerButton } from "react-hamburger-button";
import Logo from "../../assets/logo/Dark.svg";
import { withRouter } from "react-router-dom";

const MobileNavbar = ({ location }) => {
    const navbarOpen = useSelector((state) => state.GlobalState.navbarOpen);
    const dispatch = useDispatch();
    const isHome = location.pathname === "/";

    return (
        <Component>
            <HamburgerButton
                open={navbarOpen}
                className='menu-btn'
                onClick={() => {
                    dispatch({ type: "TOGGLE_NAVBAR" });
                }}
                width={24}
                height={15}
                strokeWidth={3}
                color='black'
                animationDuration={0.3}
            />
            {!isHome && <img src={Logo} alt='agm logo' className='logo' />}
            <Drawer anchor='left' open={navbarOpen} onClose={() => dispatch({ type: "TOGGLE_NAVBAR" })}></Drawer>
        </Component>
    );
};
const Component = styled.div`
    z-index: 1000;
    position: fixed;
    width: -webkit-fill-available;
    padding: 32px 16px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    .logo {
        width: 98px;
        height: auto;
    }
    @media (min-width: 768px) {
        display: none;
    }
`;
export default withRouter(MobileNavbar);
